(156, 'SY', 'A', '2', 'U', 'S', NULL, 'Site country code', NULL, 'The country in which this database site is located expressed as an ISO standard 2-letter country code.', NULL, NULL, 'This field can be set only by the Central Database Administrator.', '2022-08-22 16:14:50', '2022-08-22 16:14:50'),
(157, 'SY', 'A', '3', 'L', 'S', NULL, 'Site organisation code', NULL, 'The standard three-letter code for the organisation operating this database site.', NULL, '(1) See also 178[Organisation code], 182[Database org code].', 'This field can be set only by the Regional Database Administrator.', '2022-08-22 16:14:50', '2022-08-22 16:14:50'),
(158, 'SY', 'A', '2', 'U', 'S', NULL, 'Site default language', NULL, 'The spoken language normally used by this database site, expressed as an ISO 2-letter language code, normally in lower case.', NULL, NULL, NULL, '2022-08-22 16:14:50', '2022-08-22 16:14:50'),
(159, 'AT', NULL, NULL, NULL, NULL, NULL, 'Pre-data text (plural)', NULL, 'The plural version of text which precedes data values in a text listing.', NULL, NULL, NULL, '2022-08-22 16:14:50', '2022-08-22 16:14:50'),
(160, 'AT', NULL, NULL, NULL, NULL, NULL, 'Post-data text (plural)', NULL, 'The plural version of text which follows data values in a text listing', NULL, NULL, NULL, '2022-08-22 16:14:50', '2022-08-22 16:14:50'),
