(336, 'SY', 'A', '132', 'U', 'S', NULL, 'Local abstracts name', NULL, 'The name of the bibliographic abstracts publication which has been referred to in the \'local abstracts\' fields in the References table.', NULL, NULL, 'This field can be set only by the Regional Database Coordinator.', '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(337, 'SY', 'A', '10', 'U', 'S', NULL, 'Local abstracts initials', NULL, 'The initials or abbreviation of the bibliographic abstracts publication which has been referred to in the \'local abstracts\' fields in the References table.', '\'ASA\' for \'Australian Speleo Abstracts\'.', NULL, 'The field can be set only by the Regional Database Administrator.', '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(338, 'SY', NULL, NULL, NULL, NULL, NULL, 'AT coding-type code', NULL, 'A range of available code numbers for indicating the coding-type of a field. For example, whether it is uncoded, has a standard code, or has a local code.', NULL, NULL, NULL, '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(339, 'SY', NULL, NULL, NULL, NULL, NULL, 'AT coding-type text', NULL, 'The text corresponding to an \'AT coding-type code\'.', NULL, NULL, NULL, '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(340, 'SY', NULL, NULL, NULL, NULL, NULL, 'Field length-type code', NULL, 'The range of integer codes available to indicate a field\'s length type for data interchange purposes, e.g. whether it is a long or a sort field.', NULL, NULL, NULL, '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(341, 'SY', NULL, NULL, NULL, NULL, NULL, 'Field length-type text', NULL, 'The text corresponding to a \'Field length-type code\'.', NULL, NULL, NULL, '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(342, 'SY', NULL, NULL, NULL, NULL, NULL, 'Key status code', NULL, 'A range of integer codes to indicate whether a field is a primary or secondary key.', NULL, NULL, NULL, '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(343, 'SY', NULL, NULL, NULL, NULL, NULL, 'Key status text', NULL, 'The text corresponding to a \'key status code\'.', NULL, NULL, NULL, '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(344, 'SY', NULL, NULL, NULL, NULL, NULL, 'AT cardinality code', NULL, 'A range of integer codes to indicate whether a field is single- valued or multi-valued.', NULL, NULL, NULL, '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(345, 'SY', NULL, NULL, NULL, NULL, NULL, 'AT cardinality text', NULL, 'The text corresponding to an \'AT cardinality code\'.', NULL, NULL, NULL, '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(346, 'SY', NULL, NULL, NULL, NULL, NULL, 'Value set code', NULL, 'A range of available integer codes to indicate whether a field is set or whether it is used in a particular table.', NULL, NULL, NULL, '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(347, 'SY', NULL, NULL, NULL, NULL, NULL, 'Value set text', NULL, 'The text corresponding to a \'value set code\'.', NULL, NULL, NULL, '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(348, 'SY', NULL, NULL, NULL, NULL, NULL, 'AT data-type code', NULL, 'A range of available integer codes to indicate the data-type of a field. For example, numeric, integer, alphanumeric, etc., types.', NULL, NULL, NULL, '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(349, 'SY', NULL, NULL, NULL, NULL, NULL, 'AT data-type text', NULL, 'The text corresponding to an \'AT data-type code\'.', NULL, NULL, NULL, '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(350, 'SY', NULL, NULL, NULL, NULL, NULL, 'Language code ISO-639', NULL, 'The range of available codes from the ISO-639 international standard to indicate the language being used. These codes are two-letter lower-case codes.', NULL, NULL, NULL, '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(351, 'SY', NULL, NULL, NULL, NULL, NULL, 'Language text', NULL, 'The text corresponding to a \'Language code ISO-639\'.', NULL, NULL, NULL, '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(352, 'SY', NULL, NULL, NULL, NULL, NULL, 'SSC update-status code', NULL, 'A range of available integer codes to indicate the update status of an SSC category.', NULL, NULL, NULL, '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(353, 'SY', NULL, NULL, NULL, NULL, NULL, 'SSC update-status text', NULL, 'The text corresponding to an \'SSC update-status code\'.', NULL, NULL, NULL, '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(354, 'SY', 'A', '8', 'U', 'S', NULL, 'State abbreviation', NULL, 'The common abbreviation (as opposed to the code) for a State in a country.', NULL, NULL, NULL, '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(355, 'CA', 'A', '132', 'U', 'M', NULL, 'Ref - name of article', NULL, 'The article name of a reference cited for this cave or karst feature.', NULL, NULL, 'See usage notes 1 & 2 in 71[Ref - surnames].', '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(356, 'CA', 'A', '132', 'U', 'M', NULL, 'Ref - name of publication', NULL, 'The name of a publication cited for this cave or karst feature.', NULL, NULL, '(1) For serial publications, use the local standard abbreviations. (2) See also usage notes 1 & 2 in 71[Ref - surnames].', '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(357, 'CA', 'A', '7', 'U', 'M', NULL, 'Ref - volume number', NULL, 'The volume number of a publication cited for this cave or karst feature, without any punctuation.', NULL, 'Any punctuation will be added during output presentation.', 'See usage notes 1 & 2 in 71[Ref - surnames].', '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(358, 'CA', 'A', '4', 'U', 'M', NULL, 'Ref - issue number', NULL, 'The issue number, normally within a volume, of a publication cited for this cave or karst feature, but without any punctuation.', '\'3\' in the case of a volume and issue of 5(3).', 'Any punctuation will be provided during output presentation.', 'See usage notes 1 & 2 in 71[Ref - surnames].', '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(359, 'CA', 'A', '30', 'U', 'M', NULL, 'Map or sheet name', NULL, 'The name of the map, or sheet of a map, which has been referenced for this cave or karst feature.', NULL, '(1) This cave database field reflects the value of field 202[Map or sheet name] in the map database. (2) See also Comment (2) in field 264[Map sequence number].', 'Normally you would need to enter a value here only if: - the information was not autofilled by your lookup on the Map ID, or - the map details are not available in a map database at this site.', '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(360, 'CA', 'A', '4', 'U', 'M', NULL, 'Map & survey grade org', NULL, 'The initials of the organisation whose survey and mapping grades have been used to grade the map which has been referenced for this cave or karst feature.', NULL, '(1) This cave database field reflects the value of field 203[Map & survey grade org] from the map database. (2) See also Comment (2) in field 264[Map sequence number].', 'Normally you would need to enter a value here only if: - the information was not autofilled by your lookup on the Map ID, or - the map details are not available in a map database at this site.', '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(361, 'CA', 'A', '8', 'U', 'M', NULL, 'Map & survey grade value', NULL, 'The survey and mapping grade of a map which has been referenced for this cave or karst feature.', NULL, '(1) This cave database field reflects the value of field 204[Map & survey grade value] from the map database. (2) See also Comment (2) in field 264[Map sequence number].', 'Normally you would need to enter a value here only if: - the information was not autofilled by your lookup on the Map ID, or - the map details are not available in a map database at this site.', '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(362, 'CA', 'N', '8.', 'U', 'M', NULL, 'Map scale', NULL, 'The scale of a cave map which has been referenced for this cave or karst feature, and expressed as the ground distance of the \'representative fraction\'.', 'For a map of scale 1:1000, the value would be 1000.', '(1) This cave database field reflects the value of field 205[Map scale] from the map database. (2) See also Comment (2) in field 264[Map sequence number].', 'Normally you would need to enter a value here only if: - the information was not autofilled by your lookup on the Map ID, or - the map details are not available in a map database at this site. Max text length: sign(0)+whole(8)+sep(0)+dec(0) = Total 8, scale 0.', '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(363, 'CA', 'A', '15', 'U', 'M', NULL, 'Dates of surv/issue/amend', NULL, 'A combined textual statement of the three main dates related to a map which has been referenced for this cave or karst feature, namely, survey date, map issue date, and date of latest map amendment. A forward slash separates the three components. Examples: 1977/79/83B, 1977/in prep 79.', NULL, '(1) This cave database temporary field reflects the value of fields 207[Map survey finish year], 401[Map issue year] and 273[[Map latest update year] for this map from the map database. It will be replaced by three equivalent cave database fields. (2) See also Comment (2) in field 264[Map sequence number].', '(1) Normally you would need to enter values here only if: - the information was not autofilled by your lookup on the Map ID, or - the map details are not available in a map database at this site. (2) This is an informal text entry; year-only is sufficient for date, but include century in the first date; if surveyed but not yet issued, replace issued date by \'in prep\' + year, if appropriate; amendment letter or number can follow the amendment date.', '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(364, 'CA', 'A', '10', 'U', 'M', NULL, 'Specimen ID', NULL, 'The database internal record ID in the Specimen table which corresponds to this specimen from this cave or karst feature.', NULL, 'This is the internal database key, as distinct from any published Specimen Number.', 'This field would normally be auto-filled by a lookup on the Specimen database, which might at the same time also auto-fill other \'species found\' fields.', '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(365, 'CA', 'A', '30', 'U', 'S', NULL, 'Special land parcel', NULL, 'The name of the smallest land parcel where the cave or karst feature is located, in the case where the parcel nomenclature is not normal.', 'If the normal nomenclature at the smallest level in this area was \'Crown allotment\', but the cave was not in such a Crown allotment but was in a special reserve at Crown allotment level, then the entry here might be \'Buchan Caves Reserve\', instead of a Crown allotment number in the normal field for such allotment numbers.', NULL, NULL, '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(366, 'PL', 'A', '8', 'U', 'S', NULL, 'Map series ID', NULL, 'The record ID of the map series to which this map belongs, if any.', NULL, NULL, NULL, '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(367, 'PL', 'A', '2', 'I', 'S', 367, 'Map type', NULL, 'The type of map from the point of view of its principal use, expressed as a coded value.', 'a code to indicate \'topographic\', \'cadastral\', \'cave map\', etc.', NULL, NULL, '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(368, 'PL', 'A', '2', 'U', 'M', NULL, 'Map scope - other country', NULL, 'A further country covered by the map, expressed as the standard 2-letter country code.', NULL, NULL, NULL, '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(369, 'PL', 'A', '2', 'U', 'M', NULL, 'Map scope - other state', NULL, 'A further State covered by the map, expressed as the standard State code.', NULL, NULL, NULL, '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(370, 'PL', 'A', '2', 'U', 'S', NULL, 'Map source - country code', NULL, 'The country in which the producer of the map is located, as opposed to the country of the item depicted on the map, expressed as a standard ISO 2-letter country code.', NULL, NULL, NULL, '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(371, 'PS', 'A', '8', 'U', 'S', NULL, 'Map series ID', NULL, 'The unique database key for a Map Series record. Same principles as Cave ID.', NULL, NULL, NULL, '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(372, 'PS', 'A', '12', 'U', 'S', NULL, 'Map series abbreviation', NULL, 'The abbreviation for a map series name.', NULL, NULL, NULL, '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(373, 'PS', 'N', '10.', 'U', 'S', NULL, 'Map series scale', NULL, 'The scale used for a map series, expressed as the numeric part of a \'1:n\' expression.', 'For a 1:50 000 series, enter \'50000\'.', NULL, NULL, '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(374, 'PS', 'A', '9', 'U', 'S', NULL, 'Map series publisher ID', NULL, 'The publisher of a map series, expressed as an internal Record ID.', NULL, NULL, NULL, '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(375, 'SY', NULL, NULL, NULL, NULL, NULL, 'Sequence for lookup', NULL, 'A dummy sequence number to provide a unique lookup value for use where the lookup has duplicate values in the looked-up field. This field is needed only because of a quirk of Paradox software.', 'The Genus+Species lookup on the genus field has duplicate genus values; to allow selection of other than the first of these, a unique value for the lookup field must be provided. So we lookup on the sequence number but copy down same-named fields, i.e. genus and species only.', 'The width of this field will vary with each use of it because it must match the width of the actual field being looked up.', NULL, '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(376, 'OR', 'A', '2', 'U', 'S', NULL, 'Organisation country code', NULL, 'The country with which an organisation is concerned, expressed as a 2-letter ISO standard code.', NULL, 'International organisations should use code AA.', NULL, '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(377, 'OR', 'A', '2', 'L', 'S', NULL, 'State code', NULL, 'The State within a country in which an organisation is located, expressed as a coded value.', NULL, NULL, NULL, '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(378, 'OR', 'A', '12', 'U', 'S', NULL, 'Postcode', NULL, 'The postcode or zip code for the address of an organisation.', NULL, NULL, NULL, '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(379, 'OR', 'L', '1', 'I', 'S', 622, 'Postcode first Y/N', NULL, 'Whether the postcode of an organisation is placed first in an address line, expressed as 1 for Yes or 0 for No.', NULL, 'This allows for placing the postcode first or last when forming an address line.', NULL, '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(380, 'OR', 'A', '9', 'U', 'S', NULL, 'Organisation ID', NULL, 'The unique internal database key for an Organisation record. Same principles as for 227[Cave ID].', NULL, NULL, '(1) To prevent a different ID for the same Organisation being generated by several sites, its ID needs to be generated by a single master Organisation database site. So if you need to refer to an Organisation record which is not in your read-only Organisation database, email the Organisation\'s details to the Organisation database administrator, who will send back a new record for importing into your Org database and updating your Org lookup tables. It will be sent to all other sites at the same time.', '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(381, 'OR', 'A', '2', 'I', 'S', 381, 'Organisation type', 'Type', 'The type of organisation with respect to whether it is a club, a commercial business, a government agency, or the like. Expressed as a numeric code. Comments: 1. The lookup codes for this field contribute to the codes for 623[Source type].', NULL, NULL, NULL, '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(382, 'OR', 'L', '1', 'I', 'S', 622, 'Organisation defunct Y/N', NULL, 'Whether or not the organisation is now out of existence, expressed as 1 for Yes or 0 for No.', NULL, 'Field 384 can be used to show another organisation which may now have possession of its assets, such as maps, records, etc.', NULL, '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(383, 'OR', 'A', '4', 'U', 'S', NULL, 'Final year if defunct', NULL, 'If the organisation is now out of existence, the final year in which it was active. Expressed as 4 digits.', NULL, NULL, NULL, '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(384, 'OR', 'A', '9', 'U', 'S', NULL, 'Current org ID if defunct', NULL, 'If the organisation is no longer in existence, the Org ID (Field 380) of the organisation which now holds its assets, such as maps, reports, etc.', NULL, NULL, NULL, '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(385, 'OR', 'A', '50', 'U', 'M', NULL, 'Organisation address line', NULL, 'A line of the postal address for an organisation which comes after the organisation\'s name and before the line containing the city or suburb.', NULL, 'Typically there would be only one entry - a line containing the street and site number, however in other cases there may also be lines for building name, house name, a \'care-of\' line, or whatever else is needed to define the address. Because these lines need to be long, but are only occasionally needed, they have been separated from the main table to save space.', NULL, '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(386, 'OR', 'A', '50', 'U', 'S', NULL, 'Address line 1', NULL, 'The first line of an organisation\'s address after its name, but not including the city or suburb.', NULL, NULL, NULL, '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(387, 'OR', 'A', '50', 'U', 'S', NULL, 'Address line 2', NULL, 'The second line of an organisation\'s address, if necessary, but not including the city or suburb.', NULL, NULL, NULL, '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(388, 'OR', 'A', '50', 'U', 'S', NULL, 'Address line 3', NULL, 'The third line of an organisation\'s address, if necessary, but not including the city or suburb.', NULL, NULL, NULL, '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(389, 'OR', 'A', '50', 'U', 'S', NULL, 'Address line 4', NULL, 'The fourth line of an organisation\'s address, if necessary, but not including the city or suburb.', NULL, NULL, NULL, '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(390, 'OR', 'A', '8', 'U', 'S', NULL, 'Organisation initials', NULL, 'The official initials of an organisation, omitting the full stops.', NULL, NULL, NULL, '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(391, 'OR', 'A', '60', 'U', 'S', NULL, 'Organisation name', NULL, 'The full name of an organisation. Business descriptors such as \'Inc\' normally not included.', NULL, NULL, NULL, '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(392, 'OR', 'A', '25', 'U', 'S', NULL, 'City', NULL, 'The city or suburb component of an organisation\'s address.', NULL, NULL, NULL, '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(393, 'OR', 'A', '2', 'I', 'S', 393, 'Organisation coverage', 'Coverage', 'The geographical coverage of an organisation, expressed as a coded value. Examples: Codes for national, state, or local.', NULL, NULL, NULL, '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(394, 'OR', 'A', '1', 'I', 'S', 394, 'Organisation grouping', 'Grouping', 'Whether the an organisation consists of individuals or of other organisations.', NULL, NULL, NULL, '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(395, 'OR', 'A', '2', 'U', 'S', NULL, 'Address country code', NULL, 'The country used in the address of an organisation, expressed as a 2-letter ISO-3166 code.', NULL, 'This is distinct from Field 376, \'Organisation country code\', which shows the coverage of the organisation, e.g. European Union, Australasia, etc.', NULL, '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(396, 'PL', 'A', '2', 'I', 'S', 623, 'Map source - type', NULL, 'The type of source which produced the map, expressed as a standard code. The source could be either an organisation or a person. Comments: 1. Uses codes from generic 623[Source type].', NULL, NULL, NULL, '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(397, 'PL', 'N', '11.', 'U', 'M', NULL, 'Map scope - other N lat', NULL, 'The latitude of the northern boundary of any coverage appendage to the main mapsheet.', NULL, NULL, NULL, '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(398, 'PL', 'N', '11.', 'U', 'M', NULL, 'Map scope - other S lat', NULL, 'The latitude of the southern boundary of any coverage appendage to the main mapsheet.', NULL, NULL, NULL, '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(399, 'PL', 'N', '12.', 'U', 'M', NULL, 'Map scope - other E long', NULL, 'The longitude of the eastern boundary of any coverage appendage of the main mapsheet. Format: +/- degrees.decimal degrees.', NULL, NULL, NULL, '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(400, 'PL', 'N', '12.', 'U', 'M', NULL, 'Map scope - other W long', NULL, 'The longitude of the western boundary of any coverage appendage of the main mapsheet. Format: +/- degrees.decimal degrees.', NULL, NULL, NULL, '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(401, 'PL', 'A', '4', 'U', 'S', NULL, 'Map issue year', NULL, 'The year (in full) in which the map was originally issued.', NULL, NULL, NULL, '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(402, 'PL', 'A', '10', 'U', 'S', NULL, 'Map principal drafter ID', NULL, 'The person principally responsible for drafting the map, expressed as an internal linking Record ID.', NULL, NULL, NULL, '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(403, 'PL', 'L', '1', 'I', 'S', 622, 'Map digital master Y/N', NULL, 'Whether or not the map master copy is in digital form.', NULL, NULL, NULL, '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(404, 'PL', 'I', '4.', 'U', 'S', NULL, 'Map sheet quantity', NULL, 'The quantity of map sheets for the case where a map with a single number consists of several sheets.', NULL, 'This field is not for specifying the number of maps in a series. Max text length: sign(0)+whole(4)+sep(0)+dec(0) = Total 4, scale 0.', NULL, '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(405, 'AT', 'A', '10', NULL, 'S', NULL, 'Other field ID', NULL, 'The Record ID used by UIS for a non-standard field being used by another organisation. Uses principles similar to Cave ID (Field 227).', NULL, 'If field definitions being used by other organisations are stored, they would use the same database structure as the UIS Master Field Database, but with 82: [Standard sort field ID] being replaced by this field being defined here. This would also apply to fields being considered for adding to the standard list.', NULL, '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(406, 'PL', 'A', '9', 'U', 'S', NULL, 'Map controller org ID', NULL, 'The internal record ID of the organisation which currently controls the original or master copy of the map.', NULL, 'This field would normally only be used if the original map source no longer controls the map master copy.', NULL, '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(407, 'PL', 'A', '10', 'U', 'S', NULL, 'Map controller person ID', NULL, 'The internal record ID of the person who currently controls the original or master copy of the map. Comments: (1) This field would normally be used only if the master copy of the map was not in the original source organisation\'s map collection. (2) This field is a \'foreign key\' which would link to the person\'s details in the Person master table.', NULL, NULL, NULL, '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(408, 'PL', 'A', '70', 'U', 'S', NULL, 'Map controller comment', NULL, 'A comment about the current map controller.', NULL, 'This field is normally relevant only when the original source organisation is not the current controller of the map master copy.', NULL, '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(409, 'PL', 'A', '5', 'U', 'M', NULL, 'Map scope - other area', NULL, 'The code for further areas or regions covered by a map sheet.', NULL, 'The main area is given via Field 198.', NULL, '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(410, 'SY', 'A', '2', 'I', 'M', 410, 'Person role', NULL, 'The type of role performed by a person in a particular situation expressed as a code. The codes start at 40, in order to stay clear of Organisation Codes, so that a field could take on a value of either org type or person \'type\' without ambiguity. This is a generic system field to provide the codes.', NULL, 'A field which combines Org Type and Person Role for the case where a field could take on a value of either type is the generic field 623[Source type].', NULL, '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(411, 'CA', 'A', '10', 'U', 'M', NULL, 'Map posn - map ID', NULL, 'The site\'s UIS standard internal record ID for the map sheet upon which this grid reference is being used.', 'AUASF04567.', NULL, '(1) If no map sheet records are yet available in the database for reference, the following alternative identifying fields can be filled in: 239[Map posn - map number], 238[Map posn - map scale], and 414[Map posn - map name]. (2) If map sheet records are available in the database but the software has no link from 411 to auto-display the above three fields, then their value can be auto-supplied instead via the lookup on 411.', '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(412, 'SY', 'A', '30', 'U', 'S', NULL, 'Coarse - map series', NULL, 'A brief statement of the map series used for providing the map sheet name and grid reference for coarse locations.', '\'250K topo\' for 1:250 000 Topographic Series.', NULL, 'This field can be set only by the Central Database Administrator.', '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(413, 'SY', 'A', '30', 'U', 'S', NULL, 'Coarse - gr.ref qualifier', NULL, 'A brief statement describing the qualifier which is being used to enable distinguishing between similar grid references on the one map sheet when giving coarse locations.', '\'100K map sheet number\'', '(1) On 250K sheets you can use the \'100km square letters\' or the \'100K map sheet numbers\' to distinguish between identical grid references on the sheet.', 'This field can be set only by the Central Database Administrator.', '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(414, 'CA', 'A', '30', 'U', 'M', NULL, 'Map posn - map name', 'Map name', 'The official name of the map or mapsheet used for giving the respective grid reference for the cave or karst feature.', NULL, NULL, '(1) Normally auto-supplied when you use the lookup on the Map ID, 411[Map posn - map ID] on this screen. (2) This field needs to be actually filled in only when the map is not already in the site\'s map table, or the information is not otherwise automatically available via a lookup on the Map ID.', '2022-08-22 16:14:51', '2022-08-22 16:14:51');
INSERT INTO `fielddefinition` VALUES
(415, 'SY', 'A', '2', 'I', NULL, 415, 'Agent type', NULL, 'The type of agent who has carried out some function, with respect to whether they are a private individual, a voluntary organisation, a government department, or the like. Expressed as a numeric code.', NULL, 'This is a \'system\' value which could apply to a range of fields in a range of entities, e.g. a map producer, a land owner, a cave discoverer, etc.', NULL, '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(416, 'OR', 'A', '8', 'U', 'S', NULL, 'Initials of current org', NULL, 'If the organisation is defunct but its records are being held by another organisation, then this field shows the initials for the organisation currently holding the material.', NULL, 'This field is needed only when the information is not available by a link via the Organisation ID.', NULL, '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(417, 'CA', 'A', '9', 'U', 'S', NULL, 'Owner - organisation ID', NULL, 'The unique record ID of the owner of the cave or karst feature, if it is an organisation as opposed to a person.', NULL, NULL, '(1) If it is a person rather than an organisation, use Field 228[Owner - person ID] instead. (2) If no record IDs are available, enter the name of the person or organisation in Field 418[Owner - name].', '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(418, 'CA', 'A', '60', 'U', 'S', NULL, 'Owner - name', NULL, 'The name of the person or organisation which owns the cave or karst feature.', NULL, NULL, 'Normally this field would be included in the database only when the name is not available via a link to the owner record ID, i.e. to 228[Owner - person ID] or to 417[Owner - organisation ID].', '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(419, 'CA', 'A', '10', 'U', 'M', NULL, 'Discoverer - person ID', NULL, 'The database internal record ID of a person in the Person database associated with the respective discovery event for the cave or karst feature.', NULL, NULL, '(1) If the details of the person have not yet been entered into the linked Person database on this site, then no ID will be available for lookup, so enter the person\'s name in the nearby Name field instead. Similarly if there is no Person database on this site. (2) If more than one person is involved in this discovery event, then add a separate record for each person.', '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(420, 'CA', 'A', '9', 'U', 'M', NULL, 'Discoverer - org ID', NULL, 'The database internal record ID of an organisation in the Organisation database associated with the respective discovery event for the cave or karst feature.', NULL, NULL, '(1) If the details of the organisation have not yet been entered into the linked Organisation database on this site, then no ID will be available for lookup, so enter the Organisation initials in the nearby Name field instead. Similarly if there is no linked Organisation database on this site. (2) If more than one organisation is involved in this discovery event, and they are not covered in the person record(s), then add extra records until all organisations are included.', '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(421, 'XU', 'A', '10', 'U', 'S', NULL, 'Update ID', NULL, 'The database internal record ID for a batch of updates received from an information provider by this database site.', NULL, '(1) The updates can be in any form, e.g. paper or computerised. (2) One update batch can include more than one entity, e.g. caves and maps. (3) This field is normally not filled in by a user but by the system on this database site.', NULL, '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(422, 'XU', 'A', '10', 'U', 'S', NULL, 'Update - person ID', NULL, 'The internal record ID from the Person database identifying the person supplying a batch of updates to the database site.', NULL, '(1) This is not necessarily the same as the person originally generating the information, or the person filling in a data form, or the person who keyed the data into the database.', 'Normally the value for this field would be entered via a lookup table on the Person database.', '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(423, 'XU', 'A', '9', 'U', 'S', NULL, 'Update - organisation ID', NULL, 'The internal record ID from the Organisation database for the organisation which supplied a batch of updates to this site, usually the organisation of the person supplying the update batch.', NULL, '(1) It is this organisation which will be credited with supplying the updates contained in this Update batch.', '(1) This field is auto-filled upon an entry in field 426[Update - org code].', '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(424, 'XU', 'D', '10', 'U', 'S', NULL, 'Update - date', NULL, 'The date at which a batch of update information was supplied to the database site, as opposed to when it was entered into the database or when any of the information in individual fields was valid. The date must be a valid date.', NULL, '(1) Individual field data dates may be given in fields 472-475. (2) The key-in date is recorded in fields 429 and 449.', '(1) If the exact date is unknown, enter a best guess, including the day, because this entry must be a complete valid date.', '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(425, 'XU', 'A', '25', 'U', 'S', NULL, 'Update - person', NULL, 'The full name of the person supplying a batch of update information to the database site, as opposed to the person who generated the original data or the person who keyed it into the database. The format is Familyname comma space Givenname.', 'Smith, John', '(1) This field would normally be included in a table only when the person\'s name could not be shown via a link to their Person ID in the Person database.', '(1) The value would normally be auto-filled at the same time as performing a lookup for 422[Update - person ID] via the person database. (2) If there is no Person database, or the person\'s name is not yet in the Person Database, enter the person\'s full name in this field, observing the correct format, e.g. Smith, John. This format helps with later identification and processing of this name when adding it to a Person database.', '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(426, 'XU', 'A', '3', 'L', 'S', NULL, 'Update - org code', NULL, 'The standard 3-letter organisation code for an organisation supplying a batch of update information to the database site.', NULL, '(1) This field is normally physically present in a table only when it cannot be shown via a link to the Organisation ID. In this case, it is then normally easier for the user to enter this Org Code rather than the Org ID.', NULL, '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(427, 'XK', 'A', '10', 'U', 'S', NULL, 'Key-in ID', NULL, 'The database internal record ID identifying a data entry session, a session being any period which is considered one logical unit, and which could include entering several Update ID batches.', NULL, NULL, '(1) This field is not entered by a user but by the system.', '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(428, 'XK', 'A', '10', 'U', 'S', NULL, 'Key-in - person ID', NULL, 'The internal record ID from the Person database identifying the person who performed a data-entry session.', NULL, '(1) This field would normally be present when a Person database was available on the site, and would normally use it as a lookup. (2) If key-in records are being kept at the site, then entering this field is mandatory.', NULL, '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(429, 'XK', 'D', '10', 'U', 'S', NULL, 'Key-in - start date', NULL, 'The start date of a data entry session.', NULL, NULL, 'This field is not entered by a user but by the system.', '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(430, 'XK', 'A', '25', 'U', 'S', NULL, 'Key-in - person', NULL, 'The full name of the person performing the data entry session. The format is Familyname comma space Givenname.', 'Smith, John', '(1) This field would normally be included in a table only when the person\'s name could not be shown via a link to the Person ID in the Person database.', '(1) The value would normally be auto-filled at the same time as performing a lookup for 428[Key-in - person ID] via the Person database. (2) If there is no Person database, or the person\'s name is not yet in the Person database, enter the person\'s full name in this field, observing the correct format: Smith, John. This format helps with later identification and processing of this name when adding it to a Person database.', '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(431, 'CA', 'A', '10', 'U', 'S', NULL, 'Latest update ID', NULL, 'The database internal record ID of the most recent update batch applied to this cave.', NULL, '(1) This field is normally not completed by a user but is auto- updated when Update Batch information is captured. (2) Similar fields exist for other entities, such as maps.', NULL, '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(432, 'CA', 'A', '25', 'U', 'S', NULL, 'Latest update - person', NULL, 'The full name of the person who provided the most recent batch of updates applied to the cave, as opposed to the person who did the data entry or the person who provided the original information.', NULL, 'This field would normally be included only when the information could not be provided via the Update ID.', NULL, '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(433, 'CA', 'A', '3', 'L', 'S', NULL, 'Latest update - org code', NULL, 'The standard 3-letter organisation code for the org which provided the most recent batch of updated information applied to this cave.', NULL, 'This field would normally be used only when the organisation information could not be shown via a link to the Update ID.', NULL, '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(434, 'CA', 'D', '10', 'U', 'S', NULL, 'Latest update - date', NULL, 'The supply date of the most recent batch of update information applied to this cave, as opposed to the data entry date or to the validity date of individual fields.', NULL, 'This field is normally included only when the date can not be shown via a link to the Update ID.', NULL, '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(435, 'CA', 'A', '10', 'U', 'S', NULL, 'Latest key-in ID', NULL, 'The database internal record ID of the most recent data entry session which included this cave.', NULL, '(1) This field is normally not completed by a user but is auto-updated when Key-in Batch information is captured.', NULL, '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(436, 'CA', 'A', '25', 'U', 'S', NULL, 'Latest key-in - person', NULL, 'The full name of the person who performed the most recent data entry session for this cave.', NULL, 'This field would normally be included only when the information could not be provided via the Key-in ID.', NULL, '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(437, 'CA', 'D', '10', 'U', 'S', NULL, 'Latest key-in start date', NULL, 'The start date of the most recent data entry session which included this cave.', NULL, 'This field would normally be included only when this information could not be provided via the Key-in ID.', NULL, '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(438, 'EN', 'N', '8.', 'U', 'S', NULL, 'Site current maximum ID', NULL, 'The current maximum numeric value of any internal record ID for this entity at this site.', 'If the highest Cave ID at this site is AUASF00456 then the value of the field is 456.', 'This field is used when creating new record IDs. If a duplicate is detected, a utility program can restore this field to its correct value.', NULL, '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(439, 'CA', 'A', '30', 'U', 'S', NULL, 'Land unit - coarse', NULL, 'The smallest readily identifiable land unit in which the cave or karst feature is located, and which does not compromise the location of the cave if published.', 'In the State of Victoria in Australia, the \'Parish\' is the smallest common land unit, therefore this field would contain the name of the Parish in which the cave was located.', 'The name of the land unit series used should be specified in Field 440 in the Site Parameters.', NULL, '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(440, 'SY', 'A', '15', 'U', 'S', NULL, 'Coarse - land unit name', NULL, 'The name of the land unit series being used at this site to provide a publishable land unit location for caves or karst features which does not reveal their exact location.', 'The name is \'Parish\' in the State of Victoria in Australia.', NULL, 'This field can be set only by the Regional Database Administrator.', '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(441, 'CA', 'A', '30', 'U', 'S', NULL, 'Fifth-smallest land unit', NULL, 'The official name of the actual land unit in which this cave or karst feature is located, in the series for the 5th-smallest cadastral lands units.', 'In the hierarchy from smallest to largest of : allotment, section, parish, county, and state, this field would contain the name of the \'state\' where the cave was located. For example, if the cave entrance was located in the State of Victoria, then the entry here would be \'Victoria\'.', 'The names of the land unit types forming the land unit hierarchy, e.g. State here, are defined in the Site Parameters using the multi-valued field, 293[Land unit name].', NULL, '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(442, 'CA', 'N', '10.', 'U', 'S', NULL, 'Altitude', NULL, 'The accurate altitude of the lowest point of this cave entrance or karst feature where it leaves the profile of the surface.', NULL, NULL, 'The units can be stated in 443[Altitude - units], and the Height Datum can be given in 516[Height Datum used] if the same for all altitudes on the database site, or in 627[Altitude - height datum] if the datum may vary per cave. Max text length: sign(1)+whole(5)+sep(1)+dec(3) = total 10, scale 3.', '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(443, 'CA', 'A', '1', 'I', 'S', 298, 'Altitude - units', NULL, 'The length units in which the exact altitude has been given, expressed as a coded value.', NULL, NULL, NULL, '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(444, 'CA', 'N', '8.', 'U', 'S', NULL, 'Altitude - precision +/-', NULL, 'The +/- precision of the accurate altitude, 442[Altitude], expressed in the same units as the altitude.', 'An altitude stated as 1500m could be at a precision of 100, 10, or 1 metre. Its actual precision needs to be stated.', 'The precision is how closely (number of significant digits) the altitude has been expressed, not necessarily how accurate (true) the value is. The accuracy can be given in 445[Altitude - accuracy +/-]. Max text length: sign(0)+whole(4)+sep(1)+dec(3) = total 8, scale 3.', NULL, '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(445, 'CA', 'N', '8.', 'U', 'S', NULL, 'Altitude - accuracy +/-', NULL, 'The +/- accuracy of the \'exact position\' altitude stated in field 442[Altitude] and expressed in the same units as that altitude value.', 'An altitude stated as 1500m could be accurate to 100, 10, or 1 metre. The actual accuracy needs to be stated.', 'The accuracy is how close the given value is to the true value, not how precisely (number of significant digits) it has been expressed. The precision can be given in 444[Altitude - precision +/-] Max text length: sign(0)+whole(4)+sep(1)+dec(3) = total 8, scale 3.', NULL, '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(446, 'SY', 'A', '1', 'I', 'S', 298, 'Coarse - altitude units', NULL, 'The length units of 28[Altitude - coarse], expressed as a coded value.', NULL, NULL, 'This field can be set only by the Central Database Administrator.', '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(447, 'CA', 'L', '1', 'I', 'S', 622, 'Data awaits uploading', NULL, 'Flag to show whether further updates have been made since the last upload to a more central level. True = upload needed.', NULL, '1. If a Logical fieldtype is not available in the database software, this field can use fieldtype A1 and use coding according to field 622[True or False].', 'This field would normally be automatically updated by the system.', '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(448, 'CA', 'D', '10', 'U', 'S', NULL, 'Latest key-in end date', NULL, 'The finish date of the latest data entry session which included this cave.', NULL, 'This field would normally be included only when the information could not be provided via the Key-in ID.', NULL, '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(449, 'XK', 'D', '10', 'U', 'S', NULL, 'Key-in - end date', NULL, 'The end date of a data entry session.', NULL, '(1) This field is not entered by a user but by the system, which keeps the current session updated to the current date.', NULL, '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(450, 'XL', 'A', '10', 'U', 'S', NULL, 'Upload ID', NULL, 'The database internal record ID of a batch of data uploads to a more centralised database site in the database hierarchy. The Upload ID is issued by the organisation preparing the upload, and the Org Code in the Upload ID is their Org Code.', NULL, '(1) This field is not entered by a user but by the system at the sender\'s database site.', NULL, '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(451, 'XL', 'A', '10', 'U', 'S', NULL, 'Upload - person ID', NULL, 'The internal record ID from the Person database which identifies the person who prepared the upload.', NULL, '(1) This field would normally be present when a Person database was available at the site, and would be filled via a lookup on the Person database. (2) If Upload records are being kept at the site, then completing this field or 454[Upload - person] is mandatory.', NULL, '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(452, 'XL', 'A', '9', 'U', 'S', NULL, 'Upload - source org ID', NULL, 'The internal record ID from the Organisation database which identifies the organisation which prepared and sent the upload.', NULL, NULL, '(1) This field is auto-filled upon an entry in field 455[Upload - source org code].', '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(453, 'XL', 'D', '10', 'U', 'S', NULL, 'Upload - date', NULL, 'The date at which the upload file was prepared for sending.', NULL, NULL, 'This field is not entered by a user but by the uploader\'s system.', '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(454, 'XL', 'A', '25', 'U', 'S', NULL, 'Upload - person', NULL, 'The full name of the person who prepared the upload. The format is Familyname comma space Givenname.', 'Smith, John', '(1) This field would normally be included in a table only when the person\'s name could not be shown via a link to the Person ID in the Person database.', '(1) The value would normally be auto-filled at the same time as performing a lookup for 451[Upload - person ID] via the Person database. (2) If there is no Person database at the site, or the person\'s name is not yet in the Person database, enter the person\'s full name in this field, observing the correct format: Smith, John. This format helps with later identification and processing of this name when adding it to a Person database.', '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(455, 'XL', 'A', '3', 'L', 'S', NULL, 'Upload - source org code', NULL, 'The standard 3-letter organisation code for the organisation which prepared and sent this upload.', NULL, '(1) This field is normally physically present in a table only when it cannot be shown via a link to the Organisation ID, or when there is no Organisation database available at the site. In both cases it is normally easier to enter the Org Code.', NULL, '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(456, 'CA', 'A', '10', 'U', 'S', NULL, 'Latest upload ID', NULL, 'The database internal record ID of the most recent upload to a more central system which included this cave.', NULL, 'This field is normally not completed by a user but is auto-updated when Upload information is captured.', NULL, '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(457, 'CA', 'A', '25', 'U', 'S', NULL, 'Latest upload - person', NULL, 'The full name of the person who prepared the most recent upload which included this cave.', NULL, 'This field would normally be included only when the information was not available via the Upload ID.', NULL, '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(458, 'CA', 'A', '3', 'L', 'S', NULL, 'Latest upload - org code', NULL, 'The standard 3-letter Organisation Code of the organisation which prepared the most recent upload which included this cave.', NULL, 'This field would normally be included only when the information was not available via the Upload ID.', NULL, '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(459, 'CA', 'D', '10', 'U', 'S', NULL, 'Latest upload - date', NULL, 'The date on which the most recent upload which included this cave was prepared.', NULL, 'This field would normally be included only when the information was not available via the Upload ID.', NULL, '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(460, 'CA', 'I', '5.', 'U', 'M', NULL, 'Field ID updated', NULL, 'The Field ID of an updated field which has been included in a data upload to a more central system.', NULL, '(1) This field is not entered by a user, but is auto-filled by the system. (2) It is used for attributing updates per stakeholder organisation.', NULL, '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(461, 'CA', 'I', '5.', 'U', 'M', NULL, 'Values qty before upload', NULL, 'The quantity of values present in a given field for a cave prior to a data upload containing updates by a specific organisation for the field.', NULL, '(1) This field is not filled in by a user but by the system. (2) It is used when attributing updates to stakeholder organisations.', NULL, '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(462, 'CA', 'I', '5.', 'U', 'M', NULL, 'Values qty after upload', NULL, 'The quantity of values present in a given field for a cave after a data upload containing updates by a specific organisation for the field.', NULL, '(1) This field is not filled in by a user but by the system. (2) It is used when attributing updates to stakeholder organisations.', NULL, '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(463, 'CA', 'I', '5.', 'U', 'M', NULL, 'Qty of new/changed values', NULL, 'The quantity of values in a field which are new or changed after a data upload. It is separately recorded for a given cave, upload, and updating organisation.', '(1) 1 for an updated or newly completed single-valued field. (2) 3 if 3 values among those in a multi-valued field are new since the upload.', '(1) This field is not filled in by a user but by the system. (2) It is used when attributing updates to stakeholder organisations.', NULL, '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(464, 'CA', 'I', '5.', 'U', 'S', NULL, 'Value ID', NULL, 'The internal ID for an instance of a field value. Its format is an integer taken from a separate series per site + entity instance + field.', 'A multi-valued field for a given cave at a given database site would have its own Value ID series.', '(1) This ID is used for identifying a particular field value, for example when describing its data quality. (2) It is required only for multi-valued fields or MV field groups because values of single-valued fields can be identified by their 421[Update ID].', 'This field is not entered by the user but is auto-filled by the system.', '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(465, 'CA', 'A', '2', 'I', 'S', 465, 'Data source type', NULL, 'The type of source from which the data value was obtained, with regard to how close to or removed from the original data the supplier was, expressed as a coded value. The supplier is the person identified in 470[Data supplier - person ID] or the organisation identified in 471[Data supplier - org ID].', 'It might have been first hand information for that person, or it might have been read in a publication.', NULL, NULL, '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(466, 'CA', 'A', '2', 'I', 'S', 507, 'Data confidence level', NULL, 'The likelihood that the data value is correct, expressed as a coded value.', NULL, NULL, NULL, '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(467, 'CA', 'A', '2', 'I', 'S', 467, 'Data security level', NULL, 'The degree to which this data value can be released or published, expressed as a coded value.', NULL, NULL, NULL, '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(468, 'CA', 'A', '11', 'U', 'S', NULL, 'Data - reference ID', NULL, 'The database internal record ID from the References database which identifies the principal reference upon which the data value was based, if any.', NULL, NULL, NULL, '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(469, 'CA', 'A', '1', 'I', 'S', 469, 'Type of update', NULL, 'Whether this is a new or an updated value, expressed as a coded value.', NULL, NULL, NULL, '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(470, 'CA', 'A', '10', 'U', 'S', NULL, 'Data supplier - person ID', NULL, 'The database internal record ID from the Person database which identifies the person who is the closest known to the original data upon which this value is based.', NULL, 'Field 465[Data source type] shows the relationship of this person to the original data.', NULL, '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(471, 'CA', 'A', '9', 'U', 'S', NULL, 'Data supplier - org ID', NULL, 'The database internal record ID from the Organisation database which identifies the organisation of the person who supplied the original source information on which this field value is based.', NULL, NULL, NULL, '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(472, 'CA', 'A', '4', 'U', 'S', NULL, 'Data date - year', NULL, 'The year component of the date the information was supplied by the earliest known original information source identified in 470[Data supplier - person ID]. The format is always to be 4 digits.', NULL, NULL, NULL, '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(473, 'CA', 'A', '2', 'U', 'S', NULL, 'Data date - month', NULL, 'The month component of the date the information was supplied by the earliest known original information source. Format is in the range 1-12.', NULL, NULL, 'Leave blank if unknown.', '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(474, 'CA', 'A', '2', 'U', 'S', NULL, 'Data date - day', NULL, 'The day component of the date the information was supplied by the earliest known original information source. Format is in the range 1-31.', NULL, NULL, 'Leave blank if unknown.', '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(475, 'CA', 'A', '2', 'I', 'S', 507, 'Data date - qualifier', NULL, 'The reliability of the date given as the data date for the data upon which this field value is based. Expressed as a code value.', NULL, NULL, NULL, '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(476, 'CA', 'D', '10', 'U', 'S', NULL, 'Data date', NULL, 'The date the information was supplied by the earliest known original information source identified in 470[Data supplier - person ID]. This field is of \'date\' type.', NULL, '(1) This field is available for use where the separate date components of year, month, day are not wanted. (2) Its use does imply that a full valid date is always available for entry.', NULL, '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(477, 'CA', 'A', '2', 'I', 'M', 507, 'Discoverer date qualifier', NULL, 'The reliability of the date of the respective discovery event.', NULL, NULL, NULL, '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(478, 'PE', 'A', '10', 'U', 'S', NULL, 'Person ID', NULL, 'The unique internal database key for a Person record. Same principles as for 227[Cave ID].', NULL, NULL, '(1) To prevent a different ID for the same Person being generated by several sites, the ID needs to be generated by a single master Person database site. So if you need to refer to a Person record which is not in your read-only Person database, email the Person\'s details to the Person database administrator, who will send back a new record for importing into your Person database and updating your Person lookup tables. It will be sent to all other sites at the same time.', '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(479, 'PE', 'A', '30', 'U', 'S', NULL, 'Surname', NULL, 'The surname or family name of a person.', NULL, NULL, NULL, '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(480, 'PE', 'A', '20', 'U', 'S', NULL, 'Usual first name', 'First name', 'The firstname by which a person is usually addressed.', NULL, NULL, NULL, '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(481, 'PE', 'A', '5', 'U', 'S', NULL, 'Middle initial', NULL, 'The middle initial(s) of a person\'s name.', NULL, NULL, NULL, '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(482, 'PE', 'A', '10', 'U', 'S', NULL, 'Initials for given names', NULL, 'The full initials normally given before a person\'s surname, including that for their firstname.', NULL, NULL, NULL, '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(483, 'PE', 'A', '20', 'U', 'S', NULL, 'Title', NULL, 'The title to be used with a person in their address. Examples: Ms, Mr., Dr., Univ.Prof.Dr., etc.', NULL, NULL, NULL, '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(484, 'PE', 'A', '50', 'U', 'S', NULL, 'Address line 1', NULL, 'The first line of a person\'s address, between but excluding their name and their city/suburb. For example, address lines could include house name, building/floor details, and street details.', NULL, NULL, NULL, '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(485, 'PE', 'A', '50', 'U', 'S', NULL, 'Address line 2', NULL, 'The second line of a person\'s address, if necessary, between but excluding their name and their city/suburb.', NULL, NULL, NULL, '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(486, 'PE', 'A', '50', 'U', 'S', NULL, 'Address line 3', NULL, 'The third line of a person\'s address, if necessary, between but excluding their name and their city/suburb.', NULL, NULL, NULL, '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(487, 'PE', 'A', '50', 'U', 'S', NULL, 'Address line 4', NULL, 'The fourth line of a person\'s address, if necessary, between but excluding their name and their city/suburb.', NULL, NULL, NULL, '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(488, 'PE', 'A', '25', 'U', 'S', NULL, 'City or suburb', NULL, 'The local city or suburb of a person\'s address.', NULL, NULL, NULL, '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(489, 'PE', 'A', '8', 'U', 'S', NULL, 'State abbreviation', NULL, 'The abbreviation for the state component, if any, of a person\'s address.', NULL, NULL, NULL, '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(490, 'PE', 'A', '2', 'L', 'S', NULL, 'State code', NULL, 'The normal code for the state which will be used for the state component, if any, of a person\'s address.', NULL, NULL, NULL, '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(491, 'PE', 'A', '12', 'U', 'S', NULL, 'Postcode', NULL, 'The postcode or zip for a person\'s address.', NULL, NULL, NULL, '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(492, 'PE', 'A', '25', 'U', 'S', NULL, 'Country name', NULL, 'The name of the country used in a person\'s address.', NULL, NULL, NULL, '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(493, 'PE', 'A', '2', 'U', 'S', NULL, 'Country code', NULL, 'The ISO 2-letter code for the country used in the country component of a person\'s address.', NULL, NULL, NULL, '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(494, 'PE', 'A', '30', 'U', 'S', NULL, 'Email address', NULL, 'The Internet email address of a person.', NULL, NULL, NULL, '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(495, 'PE', 'A', '15', 'U', 'S', NULL, 'Phone numbers prefix', NULL, 'The prefix to be used with the local phone number(s) of a person to enable dialling them from outside their own country. It should include both country dialling code and area dialling code.', 'For local number 1234-5678 the prefix might be +61(3)/(03) to show international and national prefixes.', NULL, NULL, '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(496, 'PE', 'A', '18', 'U', 'S', NULL, 'Home phone number', NULL, 'The home phone number for a person, without any area or country codes. It may however include extension number details.', NULL, NULL, NULL, '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(497, 'PE', 'A', '18', 'U', 'S', NULL, 'Work phone number', NULL, 'The work phone number of a person, but without any area or country codes. It may however include extension number details.', NULL, NULL, NULL, '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(498, 'PE', 'A', '18', 'U', 'S', NULL, 'Mobile phone number', NULL, 'The mobile phone number for a person, excluding any country code.', NULL, NULL, NULL, '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(499, 'PE', 'A', '18', 'U', 'S', NULL, 'Fax phone number', NULL, 'The Fax phone number for a person, but without any area or country codes. It may however include extension number details.', NULL, NULL, NULL, '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(500, 'PE', 'A', '20', 'U', 'S', NULL, 'Pager phone & user number', NULL, 'The pager phone number and user number for a person, but without any area or country codes which are unnecessary for a local call.', NULL, NULL, NULL, '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(501, 'PE', 'A', '3', 'L', 'S', NULL, 'Organisation code 1', NULL, 'The primary relevant organisation, if any, to which a person belongs, expressed as its standard 3-letter UIS code.', NULL, NULL, NULL, '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(502, 'PE', 'A', '3', 'L', 'S', NULL, 'Organisation code 2', NULL, 'A second organisation, if any, to which a person belongs, expressed as a standard 3-letter UIS code.', NULL, NULL, NULL, '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(503, 'PE', 'A', '3', 'L', 'S', NULL, 'Organisation code 3', NULL, 'A third organisation, if any, to which a person belongs, expressed as a standard 3-letter UIS code.', NULL, NULL, NULL, '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(504, 'AT', 'A', '70', 'U', 'M', NULL, 'Field design comment line', NULL, 'A line of comment about the design of a field.', NULL, NULL, NULL, '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(505, 'OR', 'A', '8', 'U', 'S', NULL, 'State abbreviation', NULL, 'The abbreviation of the State component, if any, in an organisation\'s address.', NULL, NULL, NULL, '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(506, 'OR', 'A', '25', 'U', 'S', NULL, 'Country name', NULL, 'The name of the country in an organisation\'s address.', NULL, NULL, NULL, '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(507, 'SY', 'A', '2', 'I', 'S', 507, 'Data confidence level', NULL, 'A generic coded field for common use by data-confidence-level fields when translating their coded value.', NULL, NULL, 'When various data-confidence-level fields need their code value translated, this field should be used rather than having a separate series of values for each data-confidence-level field. A one-character symbol 508[Data confidence symbol] is also available as a translation.', '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(508, 'SY', 'A', '2', 'I', 'S', 508, 'Data confidence symbol', NULL, 'A generic coded field for a one-character symbol for use by data- confidence-level fields.', NULL, NULL, NULL, '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(509, 'CA', 'A', '2', 'I', 'M', 507, 'Genus confidence level', NULL, 'The confidence level that the genus of this specimen found in the cave has been correctly identified. Expressed as a coded value.', NULL, NULL, NULL, '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(510, 'CA', 'A', '2', 'I', 'M', 507, 'Species confidence level', NULL, 'The confidence level that the species name for this specimen found in the cave has been correctly identified. Expressed as a coded value.', NULL, NULL, NULL, '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(511, 'CA', 'N', '7.', 'U', 'S', NULL, 'Vertical extent', NULL, 'The overall vertical distance in metres between the highest and lowest known points of the cave or karst feature, expressed as an unsigned value.', NULL, 'Max text length: sign(0)+whole(4)+sep(1)+dec(2)=total 7, scale 2.', '(1) To record the extent relative to the cave entrance, use fields 59[Extent below entrance] and 60[Extent above entrance]. (2) In the case of a cave whose highest or lowest point is an entrance leading off a karst feature, such as a cave descending from the bottom of a doline, then the vertical extent of the cave should be measured from where the cave leaves the profile of the karst feature. (3) If this field is completed, then 61[Vertical method] should be completed, and also 62[Vertical accuracy] if possible. (4) Any underwater depth, if known, should be included. The depth to the water-table can be shown in 53[Comments], e.g. \'wtable -50m\'. (5) When caves join, or when there is more than one numbered entrance, this field should be completed in the nominated \'cave\' record, and left blank in any entrance records.', '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(512, 'SY', 'A', '2', 'I', 'S', 512, 'Entity update status', 'Update status', 'A generic coded field for use by entity update-status fields when translating their coded value. The status refers to how up-to-date the data is rather than how much data is stored.', NULL, NULL, NULL, '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(513, 'AT', 'I', '5.', 'U', 'S', NULL, 'Field ID used for codes', NULL, 'The field ID of the field which is used to supply the generic code values which are shared by this field.', 'All length-unit fields share a common set of length-unit codes for metres, feet, yards, etc.', NULL, NULL, '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(514, 'CA', 'N', '8.', 'U', 'S', NULL, 'Precision', NULL, 'The +/- precision of the stated full latitude and longitude values given in the \'exact position\' fields, 245[Position - latitude] and 246[Position - longitude], expressed as degrees.decimal degrees.', 'A latitude expressed as closely as +/- 1 second would have a degress.decimal degrees precision of 0.00028.', 'The precision is how closely (number of significant digits) the lat/long has been expressed, not necessarily how accurate (true) the value is. Accuracy can be expressed via field 515[Position - geog accuracy]. Max text length: sign(0)+whole(2)+sep(1)+dec(5) = total 8, scale 5.', NULL, '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(515, 'CA', 'N', '8.', 'U', 'S', NULL, 'Accuracy', NULL, 'The +/- accuracy of the stated full latitude and longitude values given in the \'exact position\' fields 245[Position - latitude] and 246[Position - longitude], expressed as degrees.decimal degrees.', 'A latitude whose value was accurate to +/- 1 second would have a degrees.decimal degrees accuracy of 0.00028.', 'The accuracy is how close the given value is to the true value, not how precisely (number of significant digits) it has been expressed. The precision can be given in field 514[Position - geog precision]. Max text length: sign(0)+whole(2)+sep(1)+dec(5) = total 8, scale 5.', NULL, '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(516, 'SY', 'A', '20', 'U', 'S', NULL, 'Height Datum used', NULL, 'The abbreviated name of the Height Datum which has been used as a basis for altitude values.', 'In Australia: \'AHD\'.', NULL, '(1) Depending on the table structure being used at your database site, this field might appear once for all altitude data, or once for each altitude entry. (2) This field can be set only by the Regional Database Administrator.', '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(517, 'PL', 'A', '3', 'U', 'S', NULL, 'Map numberer org code', NULL, 'The standard 3-letter organisation code for the organisation which issued the number for this cave/karst-related map.', NULL, 'This may differ from the source org code in the case, for example, of a caving org which numbers a cave map produced by a government agency.', NULL, '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(518, 'CA', 'A', '3', 'U', 'M', NULL, 'Map numberer org code', NULL, 'The standard 3-letter organisation code of the numberer of this map which has been referenced for this cave or karst feature.', NULL, 'This may differ from the organisation which produced the map, e.g. a caving club numbering a cave map produced by a govt agency', '(1) This field may need to be entered only when it is not automatically available via a lookup or link on 265[Widest map ID], or if the map is not yet in available in the site\'s map database.', '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(519, 'CA', 'A', '9', 'U', 'M', NULL, 'Updating organisation ID', NULL, 'The internal record ID from the Organisation database which identifies the organisation which has supplied the updates for a specific cave and field in a specific data upload to a more central system.', NULL, '(1) This field is not entered by a user but is auto-filled by the system. (2) It is used when attributing updates to stakeholder organisations.', NULL, '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(520, 'CA', 'A', '10', 'U', 'S', NULL, 'Data - key-in ID', NULL, 'The Key-in ID used for a particular field value.', NULL, NULL, 'Auto-filled by the system.', '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(521, 'CA', 'A', '10', 'U', 'S', NULL, 'Data - upload ID', NULL, 'The Upload ID used for a particular field value.', NULL, NULL, 'Auto-filled by the system.', '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(522, 'CA', 'A', '30', 'U', 'M', NULL, 'Discoverer surname', NULL, 'The surname or family name of a person involved in the initial discovery or recording aspects of this cave or karst feature.', NULL, '(1) This field is available for use in tables where the surname cannot be provided by a lookup on 419[Discoverer - person ID] either because of software limitations or because the person is not yet in the Person database.', '(1) If the person is available in the Person database, this field will be auto-filled by the 419[Discoverer - person ID] field. (2) If the Person ID is not available, manually enter the person\'s surname.', '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(523, 'CA', 'A', '20', 'U', 'M', NULL, 'Discoverer firstname', NULL, 'The firstname or given name of a person involved in the initial discovery or recording aspects of this cave or karst feature. Comments: As for 522[Discoverer surname].', NULL, NULL, 'As for 522[Discoverer surname].', '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(524, 'CA', 'A', '8', 'U', 'M', NULL, 'Discoverer - org initials', NULL, 'The normal initials of an organisation involved with the initial discovery or recording aspects of this cave or karst feature. Its format excludes full stops (periods). Comments: Similar to the Comments in 522[Discoverer surname] but based on 420[Discoverer - org ID].', NULL, NULL, 'Similar to Usage in 522[Discoverer surname] but based on 420[Discoverer - org ID].', '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(525, 'CA', 'A', '62', 'U', 'M', NULL, 'Cave description line', NULL, 'One line of description, from among one to several, for this cave or karst feature.', NULL, 'This field is for use where, for example, a memo-type field is not available in the database system. The memo-type version of this field is 530[Cave description memo].', 'When using this field, care should be taken to avoid recording only here, factual information about the cave or karst feature which can be recorded in a more useful and findable way under other fields which are more specific to that kind of information. Information which is recorded only in a general description is much harder and less reliably found in a search or report from the database.', '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(526, 'SY', 'I', NULL, 'U', 'S', NULL, 'Coarse altitude precision', NULL, 'Not yet ready. The precision to which 28[Altitude - coarse] can be given.', NULL, NULL, NULL, '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(527, 'CA', 'A', '1', 'I', 'S', 527, 'Depth category', NULL, 'The broad depth grouping to which the cave belongs, expressed as a coded value from 0 to 9, 0 meaning unknown category.', NULL, 'As for Comments in 297[Length category].', NULL, '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(528, 'CA', 'A', '2', 'U', 'S', NULL, 'Size category', NULL, 'The broad length and depth grouping to which the cave belongs, expressed as a pair of numeric codes, being the concatenation of 297[Length category] followed by 527[Depth category]. If either component is unknown, that component is set to zero.', NULL, 'As for Comment in 297[Length category].', 'As this is a \'calculated\' field, it normally does not need to be physically stored, but can be calculated when needed from its component fields.', '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(529, 'SY', 'A', '4', 'L', 'S', NULL, 'Software upgrade level', NULL, 'The upgrade level of the current system software, expressed as numeric digits, where each change to the software takes it to the next consecutive level. The level series is independent of any public \'Version number\' series.', NULL, '(1) This series allows different \'patch\' levels to be distinguished without necessarily affecting the public Version Number. (2) Storing this number in the system allows an incoming upgrade to detect the previous upgrade level and take appropriate action.', 'This field is auto-filled by the system upon each upgrade.', '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(530, 'CA', 'M', NULL, 'U', 'S', NULL, 'Cave description memo', NULL, 'A description of the cave or karst feature in a free-text memo-type field.', NULL, 'An alternative non-memo-type yet unlimited field is 525[Cave description line].', 'When using this field, care should be taken to avoid recording only here, factual information about the cave or karst feature which can be recorded in a more useful and findable way under other fields which are more specific to that kind of information. Information which is recorded only in a general description is much harder and less reliably found in a search or report from the database.', '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(531, 'CA', 'N', '5.', 'U', 'S', NULL, 'Orientation', NULL, 'The predominant orientation of the cave or karst feature expressed as degrees.decimal degrees from True North, using a 360-degree scale.', NULL, 'Max text length: sign(0)+whole(3)+sep(1)+dec(1) = total 5, scale 1.', NULL, '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(532, 'CA', 'A', '2', 'I', 'M', 1, 'Entrance feature type', NULL, 'A karst feature type associated with this entrance to the cave.', NULL, NULL, NULL, '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(533, 'CA', 'A', '3', 'I', 'M', 11, 'Entrance development', NULL, 'The morphological development of this entrance expressed as key words.', NULL, NULL, NULL, '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(534, 'CA', 'M', NULL, 'U', 'S', NULL, 'Entrance description memo', NULL, 'A description of this entrance to the cave using a free-text memo-type field.', NULL, 'An alternative non-memo-type yet unlimited field is 535[Entrance description line].', 'When using this field, care should be taken to avoid recording only here, factual information about the entrance which can be recorded in a more useful and findable way under other fields which are more specific to that kind of information. Information which is recorded only in a general description is much harder and less reliably found in a search or report from the database.', '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(535, 'CA', 'A', '62', 'U', 'M', NULL, 'Entrance description line', NULL, 'One line of description, from among one to several, of this entrance to the cave.', NULL, 'For use when the database system does not have memo-type fields available.', 'When using this field, care should be taken to avoid recording only here, factual information about the entrance which can be recorded in a more useful and findable way under other fields which are more specific to that kind of information. Information which is recorded only in a general description is much harder and less reliably found in a search or report from the database.', '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(536, 'CA', 'A', '10', 'U', 'M', NULL, 'Entrance photoID - near', NULL, 'The Photo ID of a photograph which shows a close-up view of this entrance to the cave.', NULL, 'This may link to the Photos database where the actual image may be stored.', NULL, '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(537, 'CA', 'A', '10', 'U', 'M', NULL, 'Entrance photoID - locale', NULL, 'The Photo ID of a long-shot photograph which shows the context in which this entrance is situated.', NULL, 'This may link to the Photos database where the actual image may be stored.', NULL, '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(538, 'CA', 'A', '10', 'U', 'M', NULL, 'Entrance photoID - tag', NULL, 'The Photo ID of a photograph which shows the cave or entrance number marking which identifies this entrance of the cave.', NULL, 'This may link to the Photos database where the actual image may be stored.', NULL, '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(539, 'SY', 'A', '15', 'U', 'S', NULL, 'Program version number', NULL, 'The version number of the current program, as published.', NULL, NULL, NULL, '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(540, 'CA', 'A', '10', 'U', 'M', NULL, 'Cave photo ID', NULL, 'The photo ID of a photograph of or in the cave or karst feature.', NULL, 'This may link to the Photos database where the actual image may be stored.', NULL, '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(541, 'PA', 'A', '25', 'U', 'S', NULL, 'Official land parcel ID', NULL, 'The government\'s official registration number for this land parcel on the cadastre.', NULL, NULL, NULL, '2022-08-22 16:14:51', '2022-08-22 16:14:51');
INSERT INTO `fielddefinition` VALUES
(542, 'CA', 'A', '10', 'U', 'S', NULL, 'Entrance land parcel ID', NULL, 'The database internal record ID of the smallest land parcel on which this cave entrance or karst feature is located.', NULL, NULL, 'This field can replace several land unit fields if a table for the Land Parcel entity is available on the database site. For example, it can replace several land unit fields in the Cave Exact Position table (fields 441,253-6,365).', '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(543, 'XL', 'A', '9', 'U', 'S', NULL, 'Upload - target org ID', NULL, 'The internal record ID in your database for the organisation being uploaded to.', NULL, 'The value for this field would usually be auto-filled by the program.', NULL, '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(544, 'XL', 'A', '3', 'U', 'S', NULL, 'Upload - target org code', NULL, 'The 3-letter Org Code of the target organisation in a data upload.', NULL, NULL, 'This is the field which would be filled in (via lookup) by a user to identify the target organisation in a data upload.', '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(545, 'XK', 'A', '9', 'U', 'S', NULL, 'Key-in - organisation ID', NULL, 'The internal record ID in your database for the organisation responsible for keying in a batch of updates.', NULL, NULL, 'If the person keying in the data is associated with several organisations, the program will need to allow selection of the right one for this instance. If this selection was via Org Code, then the program would probably auto-insert the respective Org ID here.', '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(546, 'XK', 'A', '3', 'U', 'S', NULL, 'Key-in - org code', NULL, 'The 3-letter organisation code for the organisation keying in an update batch.', NULL, NULL, 'This field would normally only be included where the Org Code could not be looked up via a link to 545[Key-in - organisation ID].', '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(547, 'CA', 'A', '3', 'U', 'S', NULL, 'Latest key-in - org code', NULL, 'The organisation code for the organisation which keyed in the most recent update batch.', NULL, NULL, NULL, '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(548, 'CA', 'A', '3', 'L', 'S', NULL, 'Latest upload target code', NULL, 'The organisation code for the organisation responsible for the target site of the latest upload.', NULL, NULL, NULL, '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(549, 'SY', 'A', '62', 'U', 'M', NULL, 'Data quality comment line', NULL, 'A line of comment about the quality of a specific field value.', NULL, NULL, 'This field should be used only when the comments cannot be conveyed via an existing data quality field.', '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(550, 'SY', 'M', NULL, 'U', 'S', NULL, 'Data quality comment memo', NULL, 'A comment about the quality of a specific data value using a free-text memo-type field.', NULL, 'An alternative yet unlimited non-memo-type field is 549[Data quality comment line].', 'This field should be used only when the comments cannot be conveyed via an existing data quality field.', '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(551, 'PL', 'A', '20', 'U', 'S', NULL, 'Map geodetic datum', NULL, 'The geodetic datum on which the map is based (if any) expressed in an abbreviated form such as used in a Garmin GPS unit.', NULL, NULL, NULL, '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(552, 'PL', 'A', '20', 'U', 'S', NULL, 'Map height datum', NULL, 'The height datum on which the map is based (if any), expressed in an abbreviated form.', NULL, NULL, NULL, '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(553, 'PL', 'A', '20', 'U', 'S', NULL, 'Map grid', NULL, 'The plane co-ordinate grid system used on the map (if any), expressed in an abbreviated form.', NULL, NULL, NULL, '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(554, 'PL', 'L', '1', 'I', 'S', 622, 'Map geog coords shown Y/N', NULL, 'Whether or not the map shows geographic latitiude and longitude co-ordinates.', NULL, NULL, NULL, '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(555, 'PL', 'L', '1', 'I', 'S', 622, 'Map vector version avail?', NULL, 'Whether or not a digital vector version of the map is available.', NULL, NULL, NULL, '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(556, 'PL', 'L', '1', 'I', 'S', 622, 'Map raster version avail?', NULL, 'Whether or not a digital raster version of the map is available.', NULL, NULL, NULL, '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(557, 'PL', 'A', '10', 'U', 'S', NULL, 'Map edition', NULL, 'The edition number, if any, of this map.', NULL, NULL, NULL, '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(558, 'PL', 'A', '50', 'U', 'S', NULL, 'Map special sheet name', NULL, 'The special sheet name, if any, on which this map sheet has been published instead of on its standard mapsheet name.', NULL, 'This may occur for example where a mapsheet contains only a small area of interest and is therefore included on the adjacent mapsheet.', NULL, '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(559, 'PS', 'A', '2', 'I', 'S', 367, 'Map series map type', NULL, 'The type of map that this map series is, e.g. topographical, geological, cadastral, etc. expressed as a coded value.', NULL, NULL, NULL, '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(560, 'PS', 'A', '20', 'U', 'S', NULL, 'Map series geodetic datum', NULL, 'The geodetic datum, if any, on which this map series is based, expressed in abbreviated form such as is used in a Garmin GPS unit.', NULL, NULL, 'If the datum varies from sheet to sheet then this field should be left empty and included instead in field 551[Map geodetic datum] in the individual map sheets.', '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(561, 'PS', 'A', '20', 'U', 'S', NULL, 'Map series height datum', NULL, 'The height datum, if any, used in this map series, expressed in abbreviated form.', NULL, NULL, NULL, '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(562, 'PS', 'A', '20', 'U', 'S', NULL, 'Map series map grid', NULL, 'The plane co-ordinate system map grid, if any, used in this map series, expressed in abbreviated form.', NULL, NULL, NULL, '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(563, 'PS', 'L', '1', 'I', 'S', 622, 'Map series geo coords Y/N', NULL, 'Whether or not this map series shows geographic latitude and longitude co-ordinates on the mapsheets.', NULL, NULL, NULL, '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(564, 'PS', 'L', '1', 'I', 'S', 622, 'Map series vector avail?', NULL, 'Whether or not a digital vector edition of this map series is available.', NULL, NULL, NULL, '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(565, 'PS', 'L', '1', 'I', 'S', 622, 'Map series raster avail?', NULL, 'Whether or not a digital raster edition of this map series is available.', NULL, NULL, NULL, '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(566, 'PS', 'A', '2', 'U', 'S', NULL, 'Map series source country', NULL, 'The country in which the producer of this map series is located, as opposed to the country depicted in the series.', NULL, 'Similar to 370[Map source - country code] in an individual map sheet.', NULL, '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(567, 'PS', 'A', '3', 'L', 'S', NULL, 'Map series org code', NULL, 'The three-letter code for the organisation which produced the map series.', NULL, 'Similar to field 200[Map source - org code] used in individual map sheets.', NULL, '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(568, 'PS', 'A', '2', 'I', 'S', 623, 'Map series source type', NULL, 'The type of source which produced the map series, expressed as a coded value. Comments: 1. Similar to field 396[Map source - type] as used with individual map sheets. 2. Uses codes from generic 623[Source type].', NULL, NULL, NULL, '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(569, 'PS', 'A', '2', 'U', 'S', NULL, 'Map series scope country', NULL, 'The country where the subject of the map series is located, expressed as a standard ISO 2-letter country code.', NULL, NULL, NULL, '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(570, 'PS', 'A', '2', 'L', 'S', NULL, 'Map series scope state', NULL, 'The state, if any, where the subject of the map series is located within its country, expressed as the standard local code for the state.', NULL, NULL, NULL, '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(571, 'PS', 'A', '5', 'L', 'S', NULL, 'Map series scope area', NULL, 'If required, the standard local code for the main area or region covered by the map series.', NULL, NULL, '(1) If a cave map series, this would be the cave area code. (2) If the map series covers more than one region within the state, further regions can be added via field 576[Map series other area].', '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(572, 'PS', 'A', '2', 'U', 'S', NULL, 'Map series world region', NULL, 'If required, the world region covered by the map series, expressed as a coded value.', NULL, 'This is for where the map series covers multiple countries forming a recognised region, or for a \'whole world\' series.', NULL, '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(573, 'PL', 'A', '2', 'U', 'S', NULL, 'Map scope - world region', NULL, 'If required, the world region covered by the map, expressed as a coded value. (ISO if existing)', NULL, 'This is for where the subject of the map covers multiple countries forming a recognised region, or covering the whole world.', NULL, '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(574, 'PS', 'A', '2', 'U', 'M', NULL, 'Map series other country', NULL, 'A further country covered by the map series, expressed as the ISO standard 2-letter country code.', NULL, 'Similar to 368[Map scope - other country].', NULL, '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(575, 'PS', 'A', '2', 'L', 'M', NULL, 'Map series other state', NULL, 'A further state covered by the map series within the country, expressed as the standard state code.', NULL, NULL, NULL, '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(576, 'PS', 'A', '5', 'L', 'M', NULL, 'Map series other area', NULL, 'A further area or region covered by the map series within the state, expressed as the normal coding for the area.', NULL, NULL, NULL, '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(577, 'PS', 'A', '70', 'U', 'M', NULL, 'Map series comment line', NULL, 'A line of comment about a map series.', NULL, NULL, NULL, '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(578, 'PS', 'M', NULL, 'U', 'S', NULL, 'Map series comment memo', NULL, 'A comment about the map series using a free-text memo-type field.', NULL, 'Where memo fields are not available, an alternative line-type field is available as the multi-valued 577[Map series comment line].', NULL, '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(579, 'PL', 'M', NULL, 'U', 'S', NULL, 'Map comment memo', NULL, 'A comment about the map using a free-text memo-type field.', NULL, 'Where memo fields are not available, an alternative line-type field is available as the multi-valued field 218[Map comment line].', NULL, '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(580, 'PL', 'A', '10', 'U', 'S', NULL, 'Map biblio updater ID', NULL, 'The internal record ID of the person who most recently supplied updated for this map bibliographic information.', NULL, 'If a linked Person table is not available, an alternative field which uses the person\'s actual name is 215[Map biblio updater].', NULL, '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(581, 'PL', 'A', '9', 'U', 'S', NULL, 'Map biblio updater org ID', NULL, 'The internal Record ID of the organisation which most recently supplied updates for this map bibliographic information.', NULL, 'If a linked Organisation table is not available, an alternative field which uses the organisation\'s actual initials is 216[Map biblio updater org].', NULL, '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(582, 'PL', 'A', '25', 'U', 'S', NULL, 'Map principal surveyor', NULL, 'The name of the person who was the principal surveyor in producing the survey information for the map.', NULL, 'Alternative fields are: 208[Map principal surveyor ID] where a Person db can be linked to. 584[Map surveyor] where multiple surveyor names are to be registered. 586[Map surveyor ID] for multiple surveyors linked to a Person db.', NULL, '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(583, 'PL', 'A', '25', 'U', 'S', NULL, 'Map principal drafter', NULL, 'The name of the person who was the principal drafter of the map.', NULL, 'Alternative fields are: 402[Map principal drafter ID] where a Person db can be linked to. 585[Map drafter] where multiple drafter names are to be registered. 587[Map drafter ID] for multiple drafters linked to a Person db.', NULL, '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(584, 'PL', 'A', '25', 'U', 'M', NULL, 'Map surveyor', NULL, 'The name of one of the surveyors contributing to the map.', NULL, NULL, 'This field would be used where (1) the actual name of the surveyor as opposed to their internal ID is to be stored, and (2) where more than one name may want to be stored.', '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(585, 'PL', 'A', '25', 'U', 'M', NULL, 'Map drafter', NULL, 'The name of one of the drafters of the map.', NULL, NULL, 'This field would be used where (1) the actual name of the drafter as opposed to their internal ID is to be stored, and (2) where more than one name may want to be stored.', '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(586, 'PL', 'A', '10', 'U', 'M', NULL, 'Map surveyor ID', NULL, 'The internal record ID for one of the surveyors contributing to the map.', NULL, NULL, 'This field would be used where (1) a Person db was available so that only their internal ID needed to be stored in the map details and their details viewed via the link to the Person db, and (2) more than one surveyor was wanted to be recorded against the map.', '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(587, 'PL', 'A', '10', 'U', 'M', NULL, 'Map drafter ID', NULL, 'The internal record ID for one of the people who drafted the map.', NULL, NULL, 'This field would be used where (1) a Person db was available so that only their internal Person ID needed to be stored in the map details viewed via the link to the Person db, and (2) where more than one person was wanted to be recorded as a drafter for this map.', '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(588, 'PL', 'A', '2', 'U', 'S', NULL, 'Map numberer country code', NULL, 'The country of the organisation issuing a map number, expressed as a standard ISO 2-letter country code.', NULL, NULL, NULL, '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(589, 'PL', 'B', NULL, 'U', 'M', NULL, 'Map image', NULL, 'This field contains an image of the map.', NULL, NULL, NULL, '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(590, 'PL', 'A', '50', 'U', 'M', NULL, 'Map image filename', NULL, 'The filename of an image of a map. It may or may not contain the full path.', NULL, NULL, 'This field is an alternative to 589[Map image] for use when it is not desired to store the actual image in the database. Instead, a user action can open the separate file in an appropriate graphics viewer.', '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(591, 'PL', 'A', '30', 'U', 'M', NULL, 'Map image master software', NULL, 'The name of the software, if any, used to produce the master copy of this image, e.g. Autocad, CorelDraw, Adobe Illustrator, etc', NULL, 'The version number of the software is stored in a separate field 592[Map image s/w version].', NULL, '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(592, 'PL', 'A', '10', 'U', 'M', NULL, 'Map image s/w version', NULL, 'The version number of the software which was used to create this map image.', NULL, NULL, NULL, '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(593, 'PL', 'A', '2', 'I', 'M', 593, 'Map image technique', NULL, 'The basic technique which has been used to create this image, for example, scanned, photographed, constructed, etc, expressed as a coded value.', NULL, NULL, NULL, '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(594, 'PL', 'A', '2', 'I', 'M', 594, 'Map image type', NULL, 'The type of map image, for example, still raster, still vector, VRML, rotatable wireframe, etc, expressed as a coded value.', NULL, NULL, NULL, '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(595, 'PL', 'A', '2', 'I', 'M', 595, 'Map image file format', NULL, 'The graphical format of the image file, for example, DXF - Autocad, SVG - Scalable Vector Graphics, TIFF - Tagged Image File Format, GIF - Graphics Interchange Format, etc, expressed as a coded value.', NULL, NULL, NULL, '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(596, 'SY', 'A', '50', 'U', 'S', NULL, 'Map images directory path', NULL, 'The full path to the computer disk directory where map images are stored at this database site.', NULL, NULL, NULL, '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(597, 'PL', 'A', '70', 'U', 'M', NULL, 'Map image comment', NULL, 'A comment specific to this image of the map, as opposed to this map in general.', NULL, 'Comments about the map in general can be stored in field 218[Map comment line] or 579[Map comment memo].', NULL, '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(598, 'PL', 'A', '11', 'U', 'M', NULL, 'Map citation Article ID', NULL, 'The internal record ID for a published reference for the map.', NULL, '(1) Where no References table is available, field 219[Map citation if no ID] can be used. (2) This field is a \'foreign key\' which can be used to link to the master References table.', NULL, '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(599, 'PL', 'A', '15', 'U', 'M', NULL, 'Map citation page number', NULL, 'The page number(s) on which a map appears in a cited reference, and excluding any before or after punctuation or text.', 'Single page: 5; range: 6-7;  mixed: 9-10,12', NULL, NULL, '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(600, 'PL', 'A', '70', 'U', 'M', NULL, 'Map citation comment', NULL, 'Any comment related to this map publication reference.', NULL, NULL, NULL, '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(601, 'PL', 'A', '10', 'U', 'M', NULL, 'Map scope - Cave ID', NULL, 'The internal record ID of a cave related to this map.', NULL, '(1) Where a Cave ID is not available to link to, then alternative fields to specify a cave are those specifying the components of the cave number 196[Map scope - country code], 197[Map scope - state code], 198[Map scope - area code], and 199[Map cave serial number]. (2) This field is a \'foreign key\' which can be used to link to the master Caves table.', NULL, '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(602, 'SP', 'A', '30', 'U', 'S', NULL, 'Species family', 'Family', 'The Family to which a Species belongs', NULL, NULL, NULL, '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(603, 'SP', 'A', '30', 'U', 'S', NULL, 'Species Order', 'Order', 'The Order to which a Species belongs.', NULL, NULL, NULL, '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(604, 'SP', 'A', '30', 'U', 'S', NULL, 'Species class', 'Class', 'The Class to which a Species belongs.', NULL, NULL, NULL, '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(605, 'SP', 'A', '30', 'U', 'S', NULL, 'Species phylum', 'Phylum', 'The Phylum to which a Species belongs.', NULL, NULL, NULL, '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(606, 'SP', 'A', '45', 'U', 'M', NULL, 'Species common name', 'Common name', 'The Common Name for a Species.', NULL, NULL, NULL, '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(607, 'PL', 'A', '4', 'U', 'S', NULL, 'Map survey start year', NULL, 'The year (in full) in which the survey for this map started.', NULL, NULL, NULL, '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(608, 'PB', 'A', NULL, 'U', 'S', NULL, 'ISBN', NULL, 'The International Standard Book Number (ISBN) for this publication, if any.', NULL, NULL, NULL, '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(609, 'PB', 'A', '1', 'I', 'S', 609, 'Whether author or editor', NULL, 'A flag to show whether the entry in 310[Book author or editor] is an author or an editor.', NULL, '1. A separate flag is used rather than including text in the author/editor field to allow choice of appropriate output text in each output instance.', NULL, '2022-08-22 16:14:51', '2022-12-04 11:00:59'),
(610, 'PE', 'A', '80', 'U', 'S', NULL, 'Person web page address', NULL, 'The address (URL) of this person\'s web page, if any.', NULL, NULL, NULL, '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(611, 'PE', 'A', '9', 'U', 'M', NULL, 'Person - OrgID associated', NULL, 'The internal record ID of an organisation with which this person is associated. Comments: Multiple organisations can be recorded, with the type of association or relationship described via field 612[Person - Org relationship].', NULL, NULL, NULL, '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(612, 'PE', 'A', '100', 'U', 'M', NULL, 'Person - Org relationship', NULL, 'A brief description of the relationship this person has with this organisation.', NULL, NULL, '(1) If the person is an office-bearer in the organisation, then just the name of the position could be entered, e.g. Secretary. (2) For some other kind of relationship, enter a brief description.', '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(613, 'PE', 'A', '50', 'U', 'M', NULL, 'Person address line', NULL, 'One line of address for this person, similar to 484[Address line 1]. Comments: This field allows for one or more address lines as needed.', NULL, NULL, NULL, '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(614, 'OR', 'A', '50', 'U', 'S', NULL, 'Org contact email address', NULL, 'The email address for contacting the organisation.', NULL, NULL, NULL, '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(615, 'OR', 'A', '80', 'U', 'S', NULL, 'Org web page address', NULL, 'The web page address (URL) of the organisation.', NULL, NULL, NULL, '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(616, 'RE', 'N', '11.', 'U', 'S', NULL, 'Area centroid latitude', NULL, 'The geographical latitude of the centroid of the cave/karst area, expressed as +/- degrees and decimal degrees. The sign is +ve if north of or on the Equator, or -ve if south of the Equator. Max length if text: sign(1)+whole(2)+sep(1)+dec(7) = Total 11, scale 7.', NULL, NULL, NULL, '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(617, 'RE', 'N', '12.', 'U', 'S', NULL, 'Area centroid longitude', NULL, 'The geographic longitude of the centroid of the cave/karst area, expressed as +/- degrees and decimal degrees. The sign is +ve if east of Greenwich or on the prime Meridian, or -ve if west of Greenwich or on the 180th meridian. Max length if text: sign(1)+whole(3)+sep(1)+dec(7) = Total 12, scale 7.', NULL, NULL, NULL, '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(618, 'RE', 'A', '30', 'U', 'S', NULL, 'Area 250K map sheet name', 'Sheet', 'The official map sheet name of the 1:250000 scale map sheet which covers the centroid of this cave/karst area.', NULL, 'La escala 1:250000 se usa en países con gran superficie, como Australia o Estados Unidos, pero está ausente en mapas topográficos de países más pequeños. Este campo permite enlazar a Map 195[Map ID]  o a la hoja de una serie de mapas donde se indica la escala.', NULL, '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(619, 'RE', 'A', '9', 'U', 'S', NULL, 'Area coordinating org ID', NULL, 'The internal database record ID identifying the organisation which co-ordinates work in this cave/karst area.', NULL, NULL, NULL, '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(620, 'RE', 'A', '10', 'U', 'S', NULL, 'Area numberer person ID', NULL, 'The internal database record ID identifying the person responsible for numbering caves/karst features in this cave/karst area.', NULL, NULL, NULL, '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(621, 'RE', 'A', '255', 'U', 'S', NULL, 'Area comment', NULL, 'A brief comment about this cave/karst area.', NULL, NULL, 'Normally this should be only for information which cannot be conveyed via other existing fields.', '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(622, 'SY', 'A', '1', 'I', 'S', 622, 'True or False', NULL, 'A system field to allow True or False coding where a logical field type is not available for use.', NULL, NULL, NULL, '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(623, 'SY', 'A', '2', 'I', 'S', 623, 'Source type', NULL, 'A system field for providing generic coding for fields which specify the type of organisation or role of person providing information. It combines the codes from 381[Organisation type] and 410[Person role].', NULL, NULL, NULL, '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(624, 'SY', 'A', '2', 'U', 'S', NULL, 'World region', NULL, 'A generic system field to provide codes for world regions to other fields requiring this type of entry. Comments: 1. Not yet coded. Will use ISO codes like ISO-3166 if available.', NULL, NULL, NULL, '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(625, 'CA', 'A', '20', 'U', 'S', NULL, 'Geodetic datum', NULL, 'The geodetic datum used in determining the geographic lat/long co-ords for the position of this cave or karst feature, and with the datum name expressed in abbreviated form.', 'GDA94 for Geodetic Datum of Australia 1994.', NULL, NULL, '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(626, 'CA', 'A', '20', 'U', 'S', NULL, 'Posn - gref map grid', NULL, 'The map grid used in determining the grid reference for this cave or karst feature, expressed in abbreviated form.', NULL, NULL, NULL, '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(627, 'CA', 'A', '20', 'U', 'S', NULL, 'Altitude - height datum', NULL, 'The height datum used in determining this altitude for this cave or karst feature, expressed in abbreviated form.', NULL, NULL, NULL, '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(628, 'CA', 'A', '2', 'I', 'S', 641, 'Posn - geographic method', NULL, 'The method used in determining the geographic lat/long co-ords for the position of this cave or karst feature, expressed as a coded value.', NULL, NULL, 'The associated Comment field can be used to expand upon the method if desired.', '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(629, 'CA', 'A', '20', 'U', 'M', NULL, 'Map posn - geodetic datum', 'Datum', 'The name of the geodetic datum used in determining this map grid reference for this cave or karst feature, with the datum expressed in abbreviated form.', NULL, NULL, NULL, '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(630, 'CA', 'A', '20', 'U', 'M', NULL, 'Map posn - map grid', 'Map grid', 'The name of the map grid used in determining this map grid reference for this cave or karst feature, expressed in abbreviated form.', 'MGA for Map Grid of Australia.', NULL, 'If the grid on a particular map is a local letter or letter and number grid, use \'alphanumeric\' for the grid abbreviation.', '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(631, 'CA', 'D', '10', 'U', 'S', NULL, 'Posn - geog date estimatd', NULL, 'The date on which the geographic lat/long position for this cave or karst feature was determined.', NULL, NULL, NULL, '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(632, 'CA', 'D', '10', 'U', 'M', NULL, 'Map posn - date estimated', NULL, 'The date on which this map grid reference for this cave or karst feature was determined.', NULL, NULL, NULL, '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(633, 'CA', 'A', '11', 'U', 'M', NULL, 'Map posn - citation ID', NULL, 'The database internal record ID from the References table which identifies the reference being cited to justify this grid reference.', NULL, NULL, 'This field would be included in the table only if Data Quality tables were not being used in this database - see Field 468.', '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(634, 'CA', 'A', '132', 'U', 'M', NULL, 'Map posn - citation title', NULL, 'The title of the article/paper/document which is being cited to justify this grid reference value.', NULL, NULL, 'This field would be included in the table only where the information could not be obtained via a link to Field 633, and Data Quality tables are not being used in this database.', '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(635, 'CA', 'A', '4', 'U', 'M', NULL, 'Map posn - citation year', NULL, 'The \'publication\' year of the article/paper/document, or the parent publication if any, being cited to justify this grid reference value. If the article date is earlier than the date of the parent publication, then use the article date.', NULL, NULL, 'Same as for Field 634.', '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(636, 'CA', 'A', '7', 'U', 'M', NULL, 'Map posn - citation vol', NULL, 'The volume number, if any, of the publication, if any, which contains the article or paper being cited to justify this grid reference value.', NULL, NULL, 'As for Field 634.', '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(637, 'CA', 'A', '4', 'U', 'M', NULL, 'Map posn - citation issue', NULL, 'The issue number, if any, of the publication, if any, which contains the article or paper being cited to justify this grid reference value.', NULL, NULL, 'As for 634.', '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
(638, 'CA', 'A', '13', 'U', 'M', NULL, 'Map posn - citation page', NULL, 'The page number(s) reference for the citation being used to justify this grid reference.', NULL, NULL, 'As for 633.', '2022-08-22 16:14:51', '2022-08-22 16:14:51'),
